import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { Route, Redirect } from "react-router-dom";
import { testioActions } from "./store/testio-slice";

import LoginPage from "./pages/LoginPage/LoginPage";
import HomePage from "./pages/HomePage/HomePage";
import "./index.css";

function App() {
  const dispatch = useDispatch();
  const testio = useSelector((state) => state.testio);

  useEffect(() => {
    localStorage.getItem("token") &&
      dispatch(testioActions.confirmLogin(localStorage.getItem("token")));
  }, [dispatch]);

  const logged = (
    <Route exact path="/home">
      <HomePage />
    </Route>
  );
  const notLogged = (
    <Route exact path="/home">
      <LoginPage />
    </Route>
  );

  return (
    <div>
      <Route path="/home">
        {testio.token && <HomePage />}
        {!testio.token && <Redirect to="/" />}
      </Route>
      {testio.token ? homePage : loginPage}
      <Route path="*">
        <Redirect to="/" />
      </Route>
    </div>
  );
}

export default App;
