import { createSlice } from "@reduxjs/toolkit";

const serversListSlice = createSlice({
  name: "servers",
  initialState: {
    serverLists: [],
    token: "",
    serverListIsLoading: false,
    logingIn: false,
  },
  reducers: {
    logIn(state, action) {
      state.token = action.payload;
      state.logingIn = false;
    },
    showServerList(state, action) {
      state.serverLists = action.payload;
      state.serverListIsLoading = false;
    },
  },
});

export const serversListActions = serversListSlice.actions;

export default serversListSlice;
