import "./index.css";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { logIn } from "./store/login-actions";

import LoginPage from "./components/LoginPage/LoginPage";
import { BrowserRouter as Router, Route } from "react-router-dom";
import HomePage from "./components/HomePage/HomePage";
import { runAuth } from "./utils/getServers";

function App() {
  const dispatch = useDispatch();

  /* const adminUser = {
    username: "oxylabs",
    password: "partyanimal",
  }; */
  const [user, setUser] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  /* const login = async (details) => {
    if (
      details.username === adminUser.username &&
      details.password === adminUser.password
    ) {
      await runAuth();
      setUser({
        username: details.name,
        password: details.password,
      });
    } else {
      setError("User details do not match");
    }
  }; */
  const logInHandler = (details) => {
    logIn({ username: details.username, password: details.password });
  };

  const logout = () => {
    setUser({ username: "", password: "" });
    localStorage.setItem("servers", "");
  };

  return (
    <Router>
      <div>
        {user.username !== "" ? (
          <Route
            exact
            path="/home"
            component={() => <HomePage Logout={logout} />}
          />
        ) : (
          <Route
            path="/"
            component={() => <LoginPage Login={logInHandler} error={error} />}
          />
        )}
      </div>
    </Router>
  );
}

export default App;
