import Server from "./Server";
//import { serversData } from '../data';
import React, { useState } from "react";
import { useEffect } from "react";

function ServerList() {
  const [serverList, setServerList] = useState([]);
  const [sortType, setSortType] = useState();
  useEffect(() => {
    async function getToken() {
      const response = await fetch(
        "https://playground.oxylabs.io/api/user/login",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            username: "oxylabs",
            password: "partyanimal",
          }),
        }
      );

      const data = await response.json();

      return data.token;
    }

    async function getServerList(token) {
      const response = await fetch(
        "https://playground.oxylabs.io/api/servers",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "x-access-token": token,
          },
        }
      );

      const data = await response.json();

      return data;
    }

    async function run() {
      const token = await getToken();
      const serverLists = await getServerList(token);
      setServerList(serverLists);
    }

    run();
  });






    /* const sorted = serverList.sort(function(a, b) {
      if (a.name.toLowerCase() < b.name.toLowerCase()
      ) return -1;
      if (a.name.toLowerCase() > b.name.toLowerCase()
      ) return 1;
      return 0;
    })
    console.log(sorted)
    setServerList(sorted); */

    const sorted = serverList.sort((a,b) => {
      const isReversed = (sortType === 'asc') ? 1 : -1;
      return isReversed * a.name.localeCompate(b.name)
    })


  return (
    <div className="ServerList">
      <button>Sort by Name</button>
      <table className="table table-hover">
        <thead>
          <tr className="text-uppercase table-header">
            <th scope="col">Server</th>
            <th scope="col">Distance</th>
          </tr>
        </thead>
        <tbody>
          {sorted.map((server) => {
            return (
              <Server
                key={Math.random()}
                name={server.name}
                distance={server.distance}
              ></Server>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default ServerList;
