import Server from "./Server";
import React, { useState } from "react";

function ServerList() {
  const [serverList, setServerList] = useState([]);

  async function getToken() {
    const response = await fetch(
      "https://playground.oxylabs.io/api/user/login",
      {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username: "oxylabs",
          password: "partyanimal",
        }),
      }
    );

    const data = await response.json();

    return data.token;
  }

  async function getServerList(token) {
    const response = await fetch(
      "https://playground.oxylabs.io/api/servers",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "x-access-token": token,
        },
      }
    );

    const data = await response.json();

    return data;
  }

  async function run() {
    const token = await getToken();
    const serverLists = await getServerList(token);
    localStorage.setItem("servers", JSON.stringify(serverLists));
  }

  run();



  const sortByName = () => {
    const sorted = JSON.parse(localStorage.getItem("servers")).sort(function (
      a,
      b
    ) {
      if (a.name.toLowerCase() < b.name.toLowerCase()) return -1;
      if (a.name.toLowerCase() > b.name.toLowerCase()) return 1;
      return 0;
    });
    setServerList(sorted);
  };

  const sortByDistance = () => {
    const sorted = JSON.parse(localStorage.getItem("servers")).sort(function (
      a,
      b
    ) {
      if (a.distance < b.distance) return -1;
      if (a.distance > b.distance) return 1;
      return 0;
    });
    setServerList(sorted);
  };

  return (
    <div className="ServerList">
      <button onClick={sortByName}>Show sorted by Name</button>
      <button onClick={sortByDistance}>Show sorted by Distance</button>
      <table className="table table-hover">
        <thead>
          <tr className="text-uppercase table-header">
            <th scope="col">Server</th>
            <th scope="col">Distance</th>
          </tr>
        </thead>
        <tbody>
          {serverList.map((server) => {
            return (
              <Server
                key={Math.random()}
                name={server.name}
                distance={server.distance}
              ></Server>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default ServerList;
