import { createSlice } from "@reduxjs/toolkit";

const testioSlice = createSlice({
  name: "testio",
  initialState: {
    serverLists: [],
    token: "",
    serverListIsLoading: false,
    logingIn: false,
    logged: false,
  },
  reducers: {
    confirmLogin(state, action) {
      state.user = action.payload;
      state.token = action.payload;
      state.logingIn = false;
    },
    showServerList(state, action) {
      state.serverLists = action.payload;
      state.serverListIsLoading = false;
    },
  },
});

export const testioActions = testioSlice.actions;

export default testioSlice;
