import Server from "./Server";
//import { serversData } from '../data';
import React, { useState } from "react";
import { useEffect } from "react";

function ServerList() {

  const [serverList, setServerList] = useState([]);
  useEffect(() => {
    async function getToken() {
      const response = await fetch(
        "https://playground.oxylabs.io/api/user/login",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            username: "oxylabs",
            password: "partyanimal",
          }),
        }
      );

      const data = await response.json();

      return data.token;
    }

    async function getServerList(token) {
      const response = await fetch(
        "https://playground.oxylabs.io/api/servers",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "x-access-token": token,
          },
        }
      );

      const data = await response.json();

      return data;
    }

    async function run() {
      const token = await getToken();
      const serverLists = await getServerList(token);
      setServerList(serverLists);
    }

    run();
  });

  function sortByName() {
    console.log(serverList)
  }

  return (
    <div className="ServerList">
      <button onClick={sortByName}>Sort by Name</button>
      <table className="table table-hover">
        <thead>
          <tr className="text-uppercase table-header">
            <th scope="col">Server</th>
            <th scope="col">Distance</th>
          </tr>
        </thead>
        <tbody>
          {serverList.map((server) => {
            return (
              <Server
                key={Math.random()}
                name={server.name}
                distance={server.distance}
              ></Server>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default ServerList;
