import React, { Component } from "react";

class GetServers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      servers: [],
      isLoaded: false,
    };
  }

  componentDidMount() {
    if (JSON.parse(localStorage.getItem("servers")) !== []) {
      this.setState({
        isLoaded: true,
        films: JSON.parse(localStorage.getItem("servers")),
      });
    } else {
      fetch("https://swapi.dev/api/films/")
        .then((response) => response.json())
        .then((json) => {
          localStorage.setItem("films", JSON.stringify(json.results));
          this.setState({
            isLoaded: true,
            films: JSON.parse(localStorage.getItem("servers")),
          });
        });
    }
  }

  render() {
    var { isLoaded, films } = this.state;
    if (!isLoaded) {
      return <div className="p-4 display-6">Loading data from API...</div>;
    } else {
      return (
        <div className="servers">
          <div className="">
            {films.map((film, index) => {
              return (
                <div key={index}>
                  <div>{film.title}</div>
                </div>
              );
            })}
          </div>
        </div>
      );
    }
  }
}

export default GetServers;
