import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getServerList } from "../../store/serversList-actions";

import Server from "./Server";

const ServerList = () => {
  const token = useSelector((state) => state.testio.token);
  const serversList = useSelector((state) => state.testio.serversList);
  const dispatch = useDispatch();

  const [isLoading, setIsLoading] = useState(false);
  const [loadedServers, setLoadedServers] = useState([]);

  useEffect(() => {
    return async () => {
      localStorage.getItem("servers") === null &&
        dispatch(getServerList(token));
      setIsLoading(true);
      setIsLoading(false);
      setLoadedServers(serversList);
      console.log(serversList);
    };
  }, [dispatch, serversList, token]);

  const sortByName = () => {
    const sorted = loadedServers.sort(function (a, b) {
      if (a.name.toLowerCase() < b.name.toLowerCase()) return -1;
      if (a.name.toLowerCase() > b.name.toLowerCase()) return 1;
      return 0;
    });
    setLoadedServers(sorted);
  };

  const sortByDistance = () => {
    const sorted = loadedServers.sort(function (a, b) {
      if (a.distance < b.distance) return -1;
      if (a.distance > b.distance) return 1;
      return 0;
    });
    setLoadedServers(sorted);
  };

  return (
    <div>
      <button onClick={sortByName}>Show sorted by Name</button>
      <button onClick={sortByDistance}>Show sorted by Distance</button>
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <table className="width-fill-available">
          <thead>
            <tr className="table-header">
              <th scope="text-align-start">Server</th>
              <th scope="text-align-end">Distance</th>
            </tr>
          </thead>
          <tbody>
            {loadedServers &&
              loadedServers.map((server) => {
                return (
                  <Server
                    key={Math.random()}
                    name={server.name}
                    distance={server.distance}
                  ></Server>
                );
              })}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ServerList;
