import React, { useState, useEffect, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useHistory, useLocation } from "react-router-dom";
import { getServerList } from "../../store/serversList-actions";

import Server from "./Server";

let initialLoad = true;

const ServerList = () => {
  const dispatch = useDispatch();

  const history = useHistory();
  const location = useLocation();

  const queryParams = new URLSearchParams(location.search);

  const sortOption = queryParams.get("sort");

  const testio = useSelector((state) => state.testio);
  const { serversList, token } = testio;

  const [servers, setServers] = useState([]);

  const sortHandler = useCallback(
    (option) => {
      const validOption = option ? option : "name";
      history.push(`/home?sort=${validOption}`);
      const arrayForSort = [...serversList];

      const sorted = arrayForSort.sort((a, b) => {
        return a[validOption] < b[validOption] ? -1 : 1;
      });

      setServers(sorted);
    },
    [history, serversList]
  );

  useEffect(() => {
    if (initialLoad) {
      dispatch(getServerList(token));
      initialLoad = false;
    }
    sortHandler(sortOption);
  }, [dispatch, sortHandler, sortOption, token]);

  return (
    <div>
      <select
        className="pointer"
        name="sort"
        id="sort"
        //value={ctx.sortOption}
        //onChange={(event) => ctx.sortBy(event.target.value)}
      >
        <option disabled className="textAlignStart" value="sort">
          Sort
        </option>
        <option className="textAlignStart" value="count">
          Count
        </option>
        <option className="textAlignStart" value="value">
          ABC
        </option>
      </select>

      <button onClick={() => sortHandler("name")}>Sorted by Name</button>
      <button onClick={() => sortHandler("distance")}>
        Sorted by Distance
      </button>

      <table className="width-fill-available">
        <thead>
          <tr className="table-header">
            <th scope="text-align-start">Server</th>
            <th scope="text-align-end">Distance</th>
          </tr>
        </thead>
        <tbody>
          {servers.map((server) => {
            return (
              <Server
                key={Math.random()}
                name={server.name}
                distance={server.distance}
              ></Server>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default ServerList;
