import React, { useState, useEffect } from "react";
import { testioActions } from "../../store/testio-slice";
import { useDispatch, useSelector } from "react-redux";
import { getServerList } from "../../store/serversList-actions";

import Server from "./Server";

let initialLoad = true;

const ServerList = () => {
  const token = useSelector((state) => state.testio.token);
  const serversListIsLoading = useSelector(
    (state) => state.testio.serversListIsLoading
  );
  const dispatch = useDispatch();

  const [serverList, setServerList] = useState([]);

  useEffect(() => {
    if (initialLoad) {
      localStorage.getItem("servers") === null &&
        dispatch(testioActions.getServerList(token));
      setIsLoading(true);
      initialLoad = false;
    }
  }, [dispatch, token]);

  const sortByName = () => {
    const sorted = JSON.parse(localStorage.getItem("servers")).sort(function (
      a,
      b
    ) {
      if (a.name.toLowerCase() < b.name.toLowerCase()) return -1;
      if (a.name.toLowerCase() > b.name.toLowerCase()) return 1;
      return 0;
    });
    setServerList(sorted);
  };

  const sortByDistance = () => {
    const sorted = JSON.parse(localStorage.getItem("servers")).sort(function (
      a,
      b
    ) {
      if (a.distance < b.distance) return -1;
      if (a.distance > b.distance) return 1;
      return 0;
    });
    setServerList(sorted);
  };

  return (
    <div>
      <button onClick={sortByName}>Show sorted by Name</button>
      <button onClick={sortByDistance}>Show sorted by Distance</button>
      {serversListIsLoading ? (
        <div>Loading...</div>
      ) : (
        <table className="width-fill-available">
          <thead>
            <tr className="table-header">
              <th scope="text-align-start">Server</th>
              <th scope="text-align-end">Distance</th>
            </tr>
          </thead>
          <tbody>
            {serverList.map((server) => {
              return (
                <Server
                  key={Math.random()}
                  name={server.name}
                  distance={server.distance}
                ></Server>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ServerList;
