import { Component } from "react";

class GetServers extends Component {
  constructor(props) {
    super(props);
    this.state = {
      servers: [],
    };
    this.run = this.run.bind(this)
  }


    componentDidMount() {
        async function getToken() {
            const response = await fetch('https://playground.oxylabs.io/api/user/login', {
               method: 'POST',
               headers: { "Content-Type": 'application/json' },
               body: JSON.stringify({
                 "username": "oxylabs",
                 "password": "partyanimal",
               })
             });

             const data = await response.json();

             return data.token;
          }

          async function getServerList(token) {
            const response = await fetch('https://playground.oxylabs.io/api/servers', {
               method: 'GET',
               headers: {
                 "Content-Type": 'application/json',
                 "x-access-token": token,
               },
             });

            const data = await response.json();

            return data;
          }

          async function run() {
            const token = await getToken();
            const serverList = await getServerList(token);
            this.setState({
              servers: serverList,
            });

            console.log(serverList);
          }

          run();
    }

  render() {
    const {servers} = this.state
    return (
        <div className="GetServers">{servers}</div>
    )
  }
}

export default GetServers;
