import Server from "./Server";
//import { serversData } from '../data';
import React, { useState } from "react";
import { useEffect } from "react";

function ServerList() {
  const [serverList, setServerList] = useState([]);
  useEffect(() => {
    async function getToken() {
      const response = await fetch(
        "https://playground.oxylabs.io/api/user/login",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            username: "oxylabs",
            password: "partyanimal",
          }),
        }
      );

      const data = await response.json();

      return data.token;
    }

    async function getServerList(token) {
      const response = await fetch(
        "https://playground.oxylabs.io/api/servers",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "x-access-token": token,
          },
        }
      );

      const data = await response.json();

      return data;
    }

    async function run() {
      const token = await getToken();
      const serverLists = await getServerList(token);
      setServerList(JSON.parse(localStorage.getItem("servers")));
    }

    run();
  });

  function sortByName() {
    const sorted = serverList.sort(function(a, b) {
      if (a.name.toLowerCase() < b.name.toLowerCase()
      ) return -1;
      if (a.name.toLowerCase() > b.name.toLowerCase()
      ) return 1;
      return 0;
    })
    console.log(sorted)
    return sorted
  }

  function sortByDistance() {
    const sorted = serverList.sort(function(a, b) {
      if (a.distance < b.distance
      ) return -1;
      if (a.distance > b.distance
      ) return 1;
      return 0;
    })
    console.log(sorted)
    return sorted
  }

  return (
    <div className="ServerList">
      <button onClick={() => setServerList(sortByName)}>Sort by Name</button>
      <button onClick={() => setServerList(sortByDistance)}>Sort by Distance</button>
      <table className="table table-hover">
        <thead>
          <tr className="text-uppercase table-header">
            <th scope="col">Server</th>
            <th scope="col">Distance</th>
          </tr>
        </thead>
        <tbody>
          {serverList.map((server) => {
            return (
              <Server
                key={Math.random()}
                name={server.name}
                distance={server.distance}
              ></Server>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default ServerList;
