import { createSlice } from "@reduxjs/toolkit";

const serversSlice = createSlice({
  name: "servers",
  initialState: {
    serverLists: [],
    token: "",
  },
  reducers: {
    showPeople(state, action) {
      state.people = action.payload.people;
      state.filmSelected = action.payload.title;
      state.peopleIsLoading = false;
    },
  },
});

export const serversActions = serversSlice.actions;

export default serversSlice;
