import { createSlice } from "@reduxjs/toolkit";

const serversSlice = createSlice({
  name: "films",
  initialState: {
    filmsIsLoading: false,
    peopleIsLoading: false,
    loadedCharName: "",
    films: [],
    filmSelected: "",
    people: [],
  },
  reducers: {
    setFilmsIsLoading(state) {
      state.filmsIsLoading = true;
    },
    setPeopleIsLoading(state) {
      state.peopleIsLoading = true;
    },
    loadedChar(state, action) {
      state.loadedCharName = action.payload;
    },
    showFilms(state, action) {
      state.films = action.payload.films;
      state.filmsIsLoading = false;
    },
    showPeople(state, action) {
      state.people = action.payload.people;
      state.filmSelected = action.payload.title;
      state.peopleIsLoading = false;
    },
  },
});

export const serversActions = serversSlice.actions;

export default serversSlice;
